declare enum MetadataType {
    Boolean = "BOOLEAN",
    Date = "DATE",
    DatePast = "DATE_PAST",
    DateTime = "DATE_TIME",
    Email = "EMAIL",
    LargeText = "LARGE_TEXT",
    MultiSelect = "MULTI_SELECT",
    Number = "NUMBER",
    ProjectionType = "PROJECTION_TYPE",
    Select = "SELECT",
    Speech = "SPEECH",
    State = "STATE",
    StorageSize = "STORAGE_SIZE",
    Tags = "TAGS",
    Text = "TEXT",
    TypeGuid = "TYPE_GUID",
    UserGuid = "USER_GUID",
    Views = "VIEWS"
}

declare interface PlayerParameters {
    captions: string;
    debug: boolean;
    loop: boolean;
    playerConfigurationGuid: string;
    pv: 'pipls' | 'pipss' | 'sbs';
    quality: 'auto' | 'best' | '1440p' | '1080p' | '720p' | '480p' | '240p';
    showControlPanel: boolean;
    sidebar: boolean;
    speech: string;
    speechTerm: string;
    start: number;
    volume: number;
    reporting: boolean;
    reportingId: string;
}

declare interface PlayIcon {
    height: number | string;
    position: PlayIconPosition;
    url: string;
    width: number | string;
}

declare type PlayIconPosition = 'top-left' | 'top' | 'top-right' | 'left' | 'center' | 'right' | 'bottom-left' | 'bottom' | 'bottom-right';

declare interface Presentation {
    audioOnly?: boolean;
    created?: string;
    duration?: number;
    guid?: string;
    metadata?: PresentationMetadata[];
    mediaDisplayWidth?: number;
    mediaDisplayHeight?: number;
    player?: string;
    public?: boolean;
    published?: Date;
    publisher?: PresentationUser;
    summary?: string;
    thumbnail?: PresentationThumbnail;
    title?: string;
    vod?: boolean;
}

declare interface PresentationMetadata {
    guid: string;
    html?: string;
    title: string;
    type: MetadataType;
    value?: undefined;
}

declare interface PresentationThumbnail extends QcImage {
    autoGenerated: boolean;
}

declare interface PresentationUser {
    guid: string;
    name: string;
    username: string;
}

declare class PresentationWidget {
    private readonly configurationService;
    private readonly configuration;
    private readonly presentationService;
    private presentation;
    private container;
    private destroyed;
    static create(configuration: WidgetConfiguration): Promise<PresentationWidget>;
    constructor(initialConfiguration: WidgetConfiguration);
    destroy(): void;
    init(): Promise<void>;
    private mount;
}
export { PresentationWidget }
export default PresentationWidget;

declare interface QcImage {
    cdnUrl?: string;
    height: number;
    url: string;
    width: number;
}

declare type RecursiveRecord = {
    [key: string]: string | RecursiveRecord;
};

declare interface WidgetConfiguration {
    guid: string;
    host: string;
    locales?: RecursiveRecord;
    playerParameters?: Partial<PlayerParameters>;
    selector: string | HTMLElement;
    sortBy?: string;
    sortOrder?: 'ASCENDING' | 'DESCENDING';
    widgetOptions?: Partial<WidgetOptions>;
}

declare interface WidgetOptions {
    playbackMode: 'inline' | 'inline-autoload' | 'inline-autoplay' | 'modal';
    playIcon: Partial<PlayIcon>;
    onIframeLoad(iframe: HTMLIFrameElement): void;
    onThumbnailClick(presentation: Presentation): void;
}

export { }
